INSERT INTO `widgets` (`id`, `widget_category_id`, `name`, `slug`, `description`, `type`, `file_path`, `module_ids`, `content_html`, `content_css`, `css_files`, `content_js`, `js_files`, `thumbnail`, `has_items`, `item_schema`, `settings_schema`, `is_active`, `is_core`, `created_at`, `updated_at`) VALUES (65,24,'Swiper Slider','swiper-slider','Dinamik slaytlar ekleyebileceğiniz Swiper slider','dynamic',NULL,NULL,'\n                <link rel=\"stylesheet\" href=\"https://cdn.jsdelivr.net/npm/swiper@11/swiper-bundle.min.css\">\n\n                <div class=\"swiper mySwiper-{{widget_unique_id}}\">\n                    <div class=\"swiper-wrapper\">\n                        {{#each items}}\n                        <div class=\"swiper-slide\">\n                            <img src=\"{{image}}\" alt=\"{{title}}\">\n                            <div class=\"swiper-caption\">\n                                <h3>{{title}}</h3>\n                                <p>{{description}}</p>\n                                {{#if button_text}}\n                                <a href=\"{{button_url}}\" class=\"swiper-button\">{{button_text}}</a>\n                                {{/if}}\n                            </div>\n                        </div>\n                        {{/each}}\n                    </div>\n                    \n                    <div class=\"swiper-pagination\"></div>\n                    <div class=\"swiper-button-next\"></div>\n                    <div class=\"swiper-button-prev\"></div>\n                </div>\n\n                <script src=\"https://cdn.jsdelivr.net/npm/swiper@11/swiper-bundle.min.js\"></script>\n                ','\n                .swiper {\n                    width: 100%;\n                    height: {{widget_height}}px;\n                }\n\n                .swiper-slide {\n                    text-align: center;\n                    font-size: 18px;\n                    background: #fff;\n                    display: flex;\n                    justify-content: center;\n                    align-items: center;\n                    position: relative;\n                }\n\n                .swiper-slide img {\n                    display: block;\n                    width: 100%;\n                    height: 100%;\n                    object-fit: cover;\n                }\n\n                .swiper-caption {\n                    position: absolute;\n                    bottom: 0;\n                    left: 0;\n                    right: 0;\n                    background-color: rgba(0,0,0,0.5);\n                    color: #ffffff;\n                    padding: 20px;\n                    text-align: center;\n                }\n\n                .swiper-button {\n                    display: inline-block;\n                    padding: 10px 20px;\n                    background-color: #007bff;\n                    color: white;\n                    text-decoration: none;\n                    border-radius: 5px;\n                    margin-top: 10px;\n                }\n\n                .swiper-button:hover {\n                    background-color: #0056b3;\n                }\n\n                .swiper-button-next, \n                .swiper-button-prev {\n                    color: #fff !important;\n                    background-color: rgba(0,0,0,0.5);\n                    padding: 20px;\n                    border-radius: 50%;\n                    width: 30px !important;\n                    height: 30px !important;\n                }\n\n                .swiper-pagination-bullet {\n                    width: 12px;\n                    height: 12px;\n                    background: #fff;\n                    opacity: 0.7;\n                }\n\n                .swiper-pagination-bullet-active {\n                    opacity: 1;\n                    background: #007bff;\n                }\n                ',NULL,'\n                var mySwiper = new Swiper(\".mySwiper-{{widget_unique_id}}\", {\n                    slidesPerView: 1,\n                    spaceBetween: 0,\n                    loop: true,\n                    autoplay: {\n                        delay: {{widget_autoplay_delay}},\n                        disableOnInteraction: false,\n                    },\n                    pagination: {\n                        el: \".swiper-pagination\",\n                        clickable: true,\n                    },\n                    navigation: {\n                        nextEl: \".swiper-button-next\",\n                        prevEl: \".swiper-button-prev\",\n                    },\n                    effect: \"slide\",\n                    speed: 800,\n                });\n                ',NULL,NULL,1,'[{\"name\": \"title\", \"type\": \"text\", \"label\": \"Başlık\", \"system\": true, \"required\": true, \"protected\": true}, {\"name\": \"description\", \"type\": \"textarea\", \"label\": \"Açıklama\", \"required\": false}, {\"name\": \"image\", \"type\": \"image\", \"label\": \"Görsel\", \"required\": true}, {\"name\": \"button_text\", \"type\": \"text\", \"label\": \"Buton Metni\", \"required\": false}, {\"name\": \"button_url\", \"type\": \"text\", \"label\": \"Buton Bağlantısı\", \"required\": false}, {\"name\": \"is_active\", \"type\": \"switch\", \"label\": \"Durum\", \"system\": true, \"default\": true, \"required\": false, \"protected\": true, \"properties\": {\"active_label\": \"Aktif\", \"inactive_label\": \"Aktif Değil\"}}]','[{\"name\": \"widget_height\", \"type\": \"number\", \"label\": \"Yükseklik (px)\", \"default\": 500, \"required\": false, \"properties\": {\"width\": 6, \"default_value\": 500}}, {\"name\": \"widget_autoplay_delay\", \"type\": \"number\", \"label\": \"Geçiş Hızı (ms)\", \"default\": 5000, \"required\": false, \"properties\": {\"width\": 6, \"default_value\": 5000}}, {\"name\": \"widget_autoplay\", \"type\": \"checkbox\", \"label\": \"Otomatik Oynat\", \"default\": true, \"required\": false, \"properties\": {\"width\": 12, \"default_value\": true}}, {\"name\": \"widget_unique_id\", \"type\": \"text\", \"label\": \"Benzersiz ID\", \"hidden\": true, \"system\": true, \"required\": false, \"protected\": true, \"properties\": {\"width\": 12}}]',1,1,'2025-10-05 00:43:13','2025-10-05 00:43:13');
INSERT INTO `widgets` (`id`, `widget_category_id`, `name`, `slug`, `description`, `type`, `file_path`, `module_ids`, `content_html`, `content_css`, `css_files`, `content_js`, `js_files`, `thumbnail`, `has_items`, `item_schema`, `settings_schema`, `is_active`, `is_core`, `created_at`, `updated_at`) VALUES (66,25,'Full Width Hero','full-width-hero','Sayfanın üst kısmında kullanılabilecek tam genişlikte hero bileşeni','dynamic',NULL,NULL,'<div class=\"py-5 text-center\">\n                    <div class=\"container mx-auto px-4\">\n                        <div class=\"py-8 lg:py-12\">\n                            <div class=\"max-w-3xl mx-auto\">\n                                <h1 class=\"text-3xl font-light mb-4\">{{widget_hero_title}}</h1>\n                                <h3 class=\"text-xl font-light mb-3\">{{widget_hero_subtitle}}</h3>\n                                <p class=\"text-lg mb-6\">{{widget_hero_description}}</p>\n                                <div>\n                                    {{#if widget_button_text}}\n                                    <a href=\"{{widget_button_url}}\" class=\"inline-block px-4 py-2 mr-2 mb-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition\">{{widget_button_text}}</a>\n                                    {{/if}}\n                                </div>\n                            </div>\n                        </div>\n                    </div>\n                </div>','',NULL,'',NULL,NULL,0,'[]','[{\"name\": \"widget_hero_title\", \"type\": \"text\", \"label\": \"Başlık\", \"required\": false, \"properties\": {\"width\": 12, \"placeholder\": \"Ana başlığı giriniz\", \"default_value\": \"Etkileyici Bir Başlık\"}}, {\"name\": \"widget_hero_subtitle\", \"type\": \"text\", \"label\": \"Alt Başlık\", \"required\": false, \"properties\": {\"width\": 12, \"placeholder\": \"Alt başlığı giriniz\", \"default_value\": \"Harika bir alt başlık ile devam edin.\"}}, {\"name\": \"widget_hero_description\", \"type\": \"textarea\", \"label\": \"Açıklama\", \"required\": false, \"properties\": {\"rows\": 4, \"width\": 12, \"placeholder\": \"Açıklama metnini giriniz\", \"default_value\": \"Buraya projenizi veya hizmetinizi anlatan kısa ve etkili bir açıklama yazabilirsiniz.\"}}, {\"name\": \"widget_button_text\", \"type\": \"text\", \"label\": \"Buton Metni\", \"required\": false, \"properties\": {\"width\": 6, \"placeholder\": \"Buton metnini giriniz\", \"default_value\": \"Daha Fazla Bilgi\"}}, {\"name\": \"widget_button_url\", \"type\": \"text\", \"label\": \"Buton Bağlantısı\", \"required\": false, \"properties\": {\"width\": 6, \"placeholder\": \"Buton URL\'sini giriniz\", \"default_value\": \"#\"}}, {\"name\": \"widget_unique_id\", \"type\": \"text\", \"label\": \"Benzersiz ID\", \"hidden\": true, \"system\": true, \"required\": false, \"protected\": true, \"properties\": {\"width\": 12}}]',1,1,'2025-10-05 00:43:13','2025-10-05 00:43:13');
